import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import HostDashboard from './HostDashboard';
import ShowDoMelzao from './ShowDoMelzao';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './App.css';

const socket = io(process.env.REACT_APP_SERVER_URL || 'http://localhost:5001');

function App() {
  const [gameState, setGameState] = useState<any>(null);
  const [isConnected, setIsConnected] = useState(false);
  const [showMode, setShowMode] = useState('hostDashboard'); // 'hostDashboard' or 'showMelzao'

  useEffect(() => {
    // Connection events
    socket.on('connect', () => {
      setIsConnected(true);
      toast.success('ðŸ”Œ Conectado ao servidor!');
      socket.emit('join-game');
    });

    socket.on('disconnect', () => {
      setIsConnected(false);
      toast.error('ðŸ”Œ Desconectado do servidor');
    });

    // Game events
    socket.on('game-state', setGameState);

    socket.on('participant-added', (participant: any) => {
      toast.success(`ðŸ‘¥ ${participant.name} entrou no jogo!`);
    });

    socket.on('game-started', (data: any) => {
      toast.info(`ðŸŽ® Jogo iniciado para ${data.participant.name}!`);
    });

    socket.on('answer-result', (result: any) => {
      if (result.correct) {
        toast.success(result.completed ? 'ðŸ† GANHOU O JOGO!' : 'âœ… Resposta correta!');
      } else {
        toast.error(`âŒ Resposta errada! Correto: ${result.correctAnswer}`);
      }
    });

    socket.on('time-up', () => {
      toast.warning('â° Tempo esgotado!');
    });

    socket.on('error', (error: string) => {
      toast.error(`âŒ Erro: ${error}`);
    });

    return () => {
      socket.off('connect');
      socket.off('disconnect');
      socket.off('game-state');
      socket.off('participant-added');
      socket.off('game-started');
      socket.off('answer-result');
      socket.off('time-up');
      socket.off('error');
    };
  }, []);

  return (
    <div className="min-h-screen">
      {/* Seletor de Modo */}
      <div className="fixed top-4 left-4 z-50">
        <div className="bg-white rounded-lg shadow-lg p-2 flex gap-2">
          <button
            onClick={() => setShowMode('hostDashboard')}
            className={`px-3 py-1 rounded text-sm font-medium ${
              showMode === 'hostDashboard'
                ? 'bg-blue-500 text-white'
                : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
            }`}
          >
            Host Dashboard
          </button>
          <button
            onClick={() => setShowMode('showMelzao')}
            className={`px-3 py-1 rounded text-sm font-medium ${
              showMode === 'showMelzao'
                ? 'bg-orange-500 text-white'
                : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
            }`}
          >
            Show do MelzÃ£o
          </button>
        </div>
      </div>

      {/* Status de conexÃ£o */}
      <div className={`fixed top-4 right-4 px-3 py-1 rounded text-sm z-50 ${
        isConnected
          ? 'bg-green-500 text-white'
          : 'bg-red-500 text-white animate-pulse'
      }`}>
        {isConnected ? 'ðŸŸ¢ Online' : 'ðŸ”´ Offline'}
      </div>

      {/* App principal */}
      {showMode === 'hostDashboard' ? (
        <HostDashboard socket={socket} gameState={gameState} />
      ) : (
        <ShowDoMelzao />
      )}

      {/* NotificaÃ§Ãµes */}
      <ToastContainer
        position="top-center"
        autoClose={3000}
        hideProgressBar={false}
        newestOnTop
        closeOnClick
        rtl={false}
        pauseOnFocusLoss={false}
        draggable
        pauseOnHover
        theme="dark"
      />
    </div>
  );
}

export default App;
