import React, { useState, useEffect } from 'react';

interface HostDashboardProps {
  socket: any;
  gameState: any;
}

const HostDashboard: React.FC<HostDashboardProps> = ({ socket, gameState }) => {
  const [participantName, setParticipantName] = useState('');
  const [selectedAnswer, setSelectedAnswer] = useState('');
  const [timeLeft, setTimeLeft] = useState(0);

  // Timer para resposta
  useEffect(() => {
    if (gameState?.status === 'active' && gameState.currentQuestion) {
      setTimeLeft(15);
      const timer = setInterval(() => {
        setTimeLeft(prev => {
          if (prev <= 1) {
            clearInterval(timer);
            return 0;
          }
          return prev - 1;
        });
      }, 1000);

      return () => clearInterval(timer);
    }
  }, [gameState?.currentQuestion]);

  const addParticipant = () => {
    if (participantName.trim()) {
      socket.emit('add-participant', participantName.trim());
      setParticipantName('');
    }
  };

  const startGame = (participantId: string) => {
    socket.emit('start-game', participantId);
  };

  const submitAnswer = () => {
    if (selectedAnswer && gameState.currentParticipant) {
      socket.emit('submit-answer', {
        participantId: gameState.currentParticipant.id,
        answer: selectedAnswer
      });
      setSelectedAnswer('');
    }
  };

  const quitGame = () => {
    if (gameState.currentParticipant && window.confirm('Tem certeza que quer desistir?')) {
      socket.emit('quit-game', gameState.currentParticipant.id);
    }
  };

  const resetGame = () => {
    if (window.confirm('Resetar o jogo? Todos os dados ser√£o perdidos.')) {
      socket.emit('reset-game');
    }
  };

  if (!gameState) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-white text-center">
          <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-white mx-auto mb-4"></div>
          <p>Carregando Show do Melz√£o...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto p-4 max-w-md">
      {/* Header */}
      <div className="bg-gradient-to-r from-yellow-400 to-orange-500 rounded-lg p-6 mb-6 text-center">
        <h1 className="text-3xl font-bold text-white mb-2">üéØ Show do Melz√£o</h1>
        <p className="text-yellow-100">MVP - Dashboard do Host</p>
        <div className="mt-2 text-sm">
          Status: <span className="font-bold">{gameState.status}</span> |
          Participantes: <span className="font-bold">{gameState.totalParticipants}</span>
        </div>
      </div>

      {/* Adicionar Participante */}
      {gameState.status === 'waiting' && (
        <div className="bg-gray-800 rounded-lg p-4 mb-6">
          <h2 className="text-lg font-bold text-white mb-4">üë• Adicionar Participante</h2>
          <div className="flex gap-2">
            <input
              type="text"
              value={participantName}
              onChange={(e) => setParticipantName(e.target.value)}
              placeholder="Nome do participante"
              className="flex-1 px-3 py-2 bg-gray-700 text-white rounded border border-gray-600 focus:border-blue-500"
              onKeyPress={(e) => e.key === 'Enter' && addParticipant()}
            />
            <button
              onClick={addParticipant}
              className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
            >
              ‚ûï
            </button>
          </div>
        </div>
      )}

      {/* Lista de Participantes */}
      {gameState.participants && gameState.participants.length > 0 && (
        <div className="bg-gray-800 rounded-lg p-4 mb-6">
          <h2 className="text-lg font-bold text-white mb-4">üìã Participantes</h2>
          <div className="space-y-2">
            {gameState.participants.map((participant: any) => (
              <div
                key={participant.id}
                className={`flex justify-between items-center p-3 rounded border-l-4 ${
                  participant.status === 'playing' ? 'bg-blue-700 border-blue-400' :
                  participant.status === 'winner' ? 'bg-green-700 border-green-400' :
                  participant.status === 'eliminated' ? 'bg-red-700 border-red-400' :
                  participant.status === 'quit' ? 'bg-yellow-700 border-yellow-400' :
                  'bg-gray-700 border-gray-500'
                }`}
              >
                <div>
                  <div className="text-white font-semibold">{participant.name}</div>
                  <div className="text-sm text-gray-300">
                    N√≠vel {participant.currentLevel} | {participant.totalEarned} üçØ
                  </div>
                </div>
                {gameState.status === 'waiting' && participant.status === 'waiting' && (
                  <button
                    onClick={() => startGame(participant.id)}
                    className="px-3 py-1 bg-green-600 text-white rounded text-sm hover:bg-green-700"
                  >
                    ‚ñ∂Ô∏è Iniciar
                  </button>
                )}
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Pergunta Atual */}
      {gameState.status === 'active' && gameState.currentQuestion && (
        <div className="bg-gray-800 rounded-lg p-4 mb-6">
          <div className="flex justify-between items-center mb-4">
            <h2 className="text-lg font-bold text-white">
              üéØ Pergunta {gameState.currentQuestion.level}/10
            </h2>
            <div className={`px-3 py-1 rounded-full text-white font-bold ${
              timeLeft > 10 ? 'bg-green-600' :
              timeLeft > 5 ? 'bg-yellow-600' : 'bg-red-600'
            }`}>
              ‚è∞ {timeLeft}s
            </div>
          </div>

          <div className="text-white mb-4 text-lg">
            {gameState.currentQuestion.question}
          </div>

          <div className="space-y-2 mb-4">
            {gameState.currentQuestion.options.map((option: string) => (
              <button
                key={option}
                onClick={() => setSelectedAnswer(option)}
                className={`w-full text-left p-3 rounded border transition-colors ${
                  selectedAnswer === option
                    ? 'bg-blue-600 border-blue-400 text-white'
                    : 'bg-gray-700 border-gray-600 text-gray-300 hover:bg-gray-600'
                }`}
              >
                {option}
              </button>
            ))}
          </div>

          <div className="flex gap-2">
            <button
              onClick={submitAnswer}
              disabled={!selectedAnswer}
              className="flex-1 py-2 bg-green-600 text-white rounded font-bold hover:bg-green-700 disabled:bg-gray-600 disabled:cursor-not-allowed"
            >
              ‚úÖ Confirmar Resposta
            </button>
            <button
              onClick={quitGame}
              className="px-4 py-2 bg-yellow-600 text-white rounded hover:bg-yellow-700"
            >
              üö™ Desistir
            </button>
          </div>

          <div className="mt-3 text-center text-yellow-400 font-bold">
            üí∞ Pr√™mio: {gameState.currentQuestion.honeyValue} Honey
          </div>
        </div>
      )}

      {/* Rankings */}
      {gameState.rankings && gameState.rankings.length > 0 && (
        <div className="bg-gray-800 rounded-lg p-4 mb-6">
          <h2 className="text-lg font-bold text-white mb-4">üèÜ Rankings</h2>
          <div className="space-y-2">
            {gameState.rankings.slice(0, 5).map((rank: any) => (
              <div
                key={rank.name}
                className="flex justify-between items-center p-2 bg-gray-700 rounded"
              >
                <div className="flex items-center space-x-2">
                  <span className="text-lg">
                    {rank.rank === 1 ? 'ü•á' : rank.rank === 2 ? 'ü•à' : rank.rank === 3 ? 'ü•â' : 'üî∏'}
                  </span>
                  <span className="text-white">{rank.name}</span>
                </div>
                <div className="text-right">
                  <div className="text-yellow-400 font-bold">{rank.totalEarned} üçØ</div>
                  <div className="text-xs text-gray-400">N√≠vel {rank.level}</div>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Controles do Host */}
