import React, { useState, useEffect } from 'react';
import { apiService, GameStats, TopScore, GameSession, SessionReport, QuestionStats } from '../services/api';
import SimpleCharts from './SimpleCharts';

const HistoryDashboard: React.FC = () => {
  const [activeTab, setActiveTab] = useState<'stats' | 'leaderboard' | 'sessions' | 'questions' | 'charts'>('stats');
  const [gameStats, setGameStats] = useState<GameStats | null>(null);
  const [topScores, setTopScores] = useState<TopScore[]>([]);
  const [sessions, setSessions] = useState<GameSession[]>([]);
  const [selectedSession, setSelectedSession] = useState<SessionReport | null>(null);
  const [questionStats, setQuestionStats] = useState<QuestionStats[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const loadGameStats = async () => {
    try {
      setLoading(true);
      setError(null);
      const stats = await apiService.getGameStats();
      setGameStats(stats);
    } catch (err) {
      setError('Erro ao carregar estat√≠sticas');
    } finally {
      setLoading(false);
    }
  };

  const loadTopScores = async () => {
    try {
      setLoading(true);
      setError(null);
      const scores = await apiService.getTopScores();
      setTopScores(scores);
    } catch (err) {
      setError('Erro ao carregar ranking');
    } finally {
      setLoading(false);
    }
  };

  const loadSessions = async () => {
    try {
      setLoading(true);
      setError(null);
      const sessionList = await apiService.getGameSessions(30);
      setSessions(sessionList);
    } catch (err) {
      setError('Erro ao carregar sess√µes');
    } finally {
      setLoading(false);
    }
  };

  const loadSessionDetail = async (sessionId: string) => {
    try {
      setLoading(true);
      setError(null);
      const report = await apiService.getSessionReport(sessionId);
      setSelectedSession(report);
    } catch (err) {
      setError('Erro ao carregar detalhes da sess√£o');
    } finally {
      setLoading(false);
    }
  };

  const loadQuestionStats = async () => {
    try {
      setLoading(true);
      setError(null);
      const stats = await apiService.getQuestionStats();
      setQuestionStats(stats);
    } catch (err) {
      setError('Erro ao carregar estat√≠sticas de perguntas');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    switch (activeTab) {
      case 'stats':
        loadGameStats();
        break;
      case 'leaderboard':
        loadTopScores();
        break;
      case 'sessions':
        loadSessions();
        break;
      case 'questions':
        loadQuestionStats();
        break;
      case 'charts':
        loadTopScores();
        loadQuestionStats();
        break;
    }
  }, [activeTab]);

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleString('pt-BR');
  };

  const renderStats = () => (
    <div className="space-y-4">
      {gameStats && (
        <>
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
            <div className="card text-center">
              <div className="text-2xl font-bold text-blue-400">{gameStats.totalSessions}</div>
              <div className="text-sm text-gray-300">Sess√µes Totais</div>
            </div>
            <div className="card text-center">
              <div className="text-2xl font-bold text-green-400">{gameStats.totalParticipants}</div>
              <div className="text-sm text-gray-300">Participantes</div>
            </div>
            <div className="card text-center">
              <div className="text-2xl font-bold text-yellow-400">{gameStats.totalWinners}</div>
              <div className="text-sm text-gray-300">Vencedores</div>
            </div>
            <div className="card text-center">
              <div className="text-2xl font-bold text-purple-400">{gameStats.accuracyRate}%</div>
              <div className="text-sm text-gray-300">Taxa de Acerto</div>
            </div>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div className="card">
              <h3 className="text-lg font-bold text-white mb-2">üìä Respostas</h3>
              <div className="space-y-2">
                <div className="flex justify-between">
                  <span>Total de Respostas:</span>
                  <span className="font-bold">{gameStats.totalAnswers}</span>
                </div>
                <div className="flex justify-between">
                  <span>Respostas Corretas:</span>
                  <span className="font-bold text-green-400">{gameStats.correctAnswers}</span>
                </div>
                <div className="flex justify-between">
                  <span>Respostas Erradas:</span>
                  <span className="font-bold text-red-400">{gameStats.totalAnswers - gameStats.correctAnswers}</span>
                </div>
              </div>
            </div>

            <div className="card">
              <h3 className="text-lg font-bold text-white mb-2">üçØ Honey M√©dio</h3>
              <div className="text-3xl font-bold text-yellow-400 text-center">
                {gameStats.averageHoneyEarned}
              </div>
              <div className="text-sm text-gray-300 text-center mt-1">Honey por jogador</div>
            </div>
          </div>
        </>
      )}
    </div>
  );

  const renderLeaderboard = () => (
    <div className="card">
      <h3 className="text-lg font-bold text-white mb-4">üèÜ Top 10 Maiores Pontua√ß√µes</h3>
      <div className="space-y-2">
        {topScores.map((score, index) => (
          <div
            key={`${score.name}-${score.session_date}`}
            className="flex justify-between items-center p-3 bg-gray-700 rounded"
          >
            <div className="flex items-center gap-3">
              <span className="text-2xl">
                {index === 0 ? 'ü•á' : index === 1 ? 'ü•à' : index === 2 ? 'ü•â' : `${index + 1}¬∞`}
              </span>
              <div>
                <div className="font-bold text-white">{score.name}</div>
                <div className="text-xs text-gray-400">
                  {formatDate(score.session_date)} ‚Ä¢ Status: {score.final_status}
                </div>
              </div>
            </div>
            <div className="text-right">
              <div className="text-xl font-bold text-yellow-400">{score.total_earned} üçØ</div>
              <div className="text-sm text-gray-300">N√≠vel {score.final_level}</div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );

  const renderSessions = () => (
    <div className="space-y-4">
      {selectedSession ? (
        <div className="space-y-4">
          <div className="flex items-center gap-2 mb-4">
            <button
              onClick={() => setSelectedSession(null)}
              className="btn bg-gray-600 px-3 py-1 text-sm"
            >
              ‚Üê Voltar
            </button>
            <h3 className="text-lg font-bold text-white">
              Detalhes da Sess√£o: {selectedSession.session.session_id}
            </h3>
          </div>

          <div className="card">
            <h4 className="font-bold text-white mb-2">‚ÑπÔ∏è Informa√ß√µes da Sess√£o</h4>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm">
              <div>Iniciada: {formatDate(selectedSession.session.started_at)}</div>
              <div>Finalizada: {selectedSession.session.ended_at ? formatDate(selectedSession.session.ended_at) : 'Em andamento'}</div>
              <div>Status: {selectedSession.session.status}</div>
              <div>Total de Participantes: {selectedSession.session.total_participants}</div>
            </div>
          </div>

          <div className="card">
            <h4 className="font-bold text-white mb-4">üë• Participantes</h4>
            <div className="space-y-3">
              {selectedSession.participants.map((participant) => (
                <div key={participant.participant_id} className="bg-gray-700 rounded p-3">
                  <div className="flex justify-between items-center mb-2">
                    <div className="font-bold text-white">{participant.name}</div>
                    <div className="text-sm">
                      <span className={`px-2 py-1 rounded text-xs ${
                        participant.final_status === 'winner' ? 'bg-green-600' :
                        participant.final_status === 'eliminated' ? 'bg-red-600' :
                        participant.final_status === 'quit' ? 'bg-yellow-600' :
                        'bg-gray-600'
                      }`}>
                        {participant.final_status || 'Em jogo'}
                      </span>
                    </div>
                  </div>
                  <div className="text-sm text-gray-300 mb-2">
                    N√≠vel {participant.final_level} ‚Ä¢ {participant.total_earned} üçØ ‚Ä¢
                    {participant.answers.filter(a => a.is_correct).length}/{participant.answers.length} corretas
                  </div>

                  {participant.answers.length > 0 && (
                    <div className="mt-2">
                      <div className="text-xs text-gray-400 mb-1">Respostas:</div>
                      <div className="space-y-1">
                        {participant.answers.slice(-3).map((answer) => (
                          <div key={answer.id} className="text-xs flex items-center gap-2">
                            <span className={answer.is_correct ? '‚úÖ' : '‚ùå'}></span>
                            <span className="truncate">N√≠vel {answer.level}: {answer.question_text.substring(0, 50)}...</span>
                          </div>
                        ))}
                      </div>
                    </div>
                  )}
                </div>
              ))}
            </div>
          </div>
        </div>
      ) : (
        <div className="card">
          <h3 className="text-lg font-bold text-white mb-4">üìã √öltimas 30 Sess√µes</h3>
          <div className="space-y-2">
            {sessions.map((session) => (
              <div
                key={session.session_id}
                className="flex justify-between items-center p-3 bg-gray-700 rounded hover:bg-gray-600 cursor-pointer"
                onClick={() => loadSessionDetail(session.session_id)}
              >
                <div>
                  <div className="font-bold text-white">{session.session_id}</div>
                  <div className="text-sm text-gray-300">
                    {formatDate(session.started_at)} ‚Ä¢ {session.total_participants} participantes
                  </div>
                </div>
                <div className="text-right">
                  <div className={`px-2 py-1 rounded text-xs ${
                    session.status === 'active' ? 'bg-green-600' : 'bg-gray-600'
                  }`}>
                    {session.status}
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );

  const renderQuestions = () => (
    <div className="card">
      <h3 className="text-lg font-bold text-white mb-4">‚ùì Estat√≠sticas de Perguntas</h3>
      <div className="space-y-2 max-h-96 overflow-y-auto">
        {questionStats.map((stat) => (
          <div key={stat.question_id} className="bg-gray-700 rounded p-3">
            <div className="text-white font-medium mb-1">
              N√≠vel {stat.level}: {stat.question_text.substring(0, 80)}...
            </div>
            <div className="grid grid-cols-3 gap-4 text-sm text-gray-300">
              <div>Perguntada: {stat.times_asked}x</div>
              <div>Acertos: {stat.correct_count}</div>
              <div className={`font-bold ${
                stat.accuracy_rate > 70 ? 'text-green-400' :
                stat.accuracy_rate > 40 ? 'text-yellow-400' : 'text-red-400'
              }`}>
                {stat.accuracy_rate.toFixed(1)}% acerto
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );

  return (
    <div className="space-y-4">
      {/* Navega√ß√£o */}
      <div className="card">
        <div className="flex flex-wrap gap-2">
          {[
            { key: 'stats', label: 'üìä Estat√≠sticas', icon: 'üìä' },
            { key: 'leaderboard', label: 'üèÜ Ranking', icon: 'üèÜ' },
            { key: 'sessions', label: 'üìã Sess√µes', icon: 'üìã' },
            { key: 'questions', label: '‚ùì Perguntas', icon: '‚ùì' },
            { key: 'charts', label: 'üìà Gr√°ficos', icon: 'üìà' }
          ].map(tab => (
            <button
              key={tab.key}
              onClick={() => setActiveTab(tab.key as any)}
              className={`btn px-4 py-2 ${
                activeTab === tab.key ? 'btn-primary' : 'bg-gray-600'
              }`}
            >
              {tab.label}
            </button>
          ))}
        </div>
      </div>

      {/* Conte√∫do */}
      {loading && (
        <div className="text-center text-white py-8">
          <div className="animate-spin w-8 h-8 border-2 border-blue-500 border-t-transparent rounded-full mx-auto mb-2"></div>
          Carregando dados hist√≥ricos...
        </div>
      )}

      {error && (
        <div className="card bg-red-900 border-red-500">
          <div className="text-red-200">‚ùå {error}</div>
          <button
            onClick={() => window.location.reload()}
            className="btn btn-danger mt-2 text-sm"
          >
            Tentar novamente
          </button>
        </div>
      )}

      {!loading && !error && (
        <>
          {activeTab === 'stats' && renderStats()}
          {activeTab === 'leaderboard' && renderLeaderboard()}
          {activeTab === 'sessions' && renderSessions()}
          {activeTab === 'questions' && renderQuestions()}
          {activeTab === 'charts' && (
            <SimpleCharts topScores={topScores} questionStats={questionStats} />
          )}
        </>
      )}
    </div>
  );
};

export default HistoryDashboard;
